if TestDatum '= Comparison {
	w !,"Inconsistency encountered while testing "_Name
	w !!,"Test Data:",!
	zzdump TestDatum
	w !!,"Comparison Data:",!
	zzdump Comparison
	w !
	return 0
} else {
	return 1
